INSERT INTO `md_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_thumb2`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('66', '21', 'ECS000066', '岛津(Shimadzu) 双光束紫外可见分光光度计 UV-1800', '+', '0', '12', '', '0', '0.000', '0.00', '0.00', '0.00', '0', '0', '0', '', '', '<p><span style=\"font-family: Arial, Helvetica, 宋体; font-size: 13px; background-color: rgb(244, 247, 250);\">岛津(Shimadzu) 双光束紫外可见分光光度计 UV-1800UV-1800外观小巧新颖，流线外形设计。应用Czerny-Turner分光镜，并取得同级别产品中最高级别的分辨率（1nm），实现明亮的光学系统，紧凑的尺寸（安装面积：W450&times;D490mm）。可用作单机仪器，也可计算机控制，内置USB I/F支持USB闪存驱动，加强了与计算机的配合。</span></p>\r\n<p>&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体; font-size: 14px; font-weight: bold;\">产品信息</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">UV-1800是2008年岛津UV新产品，代替岛津原型号UV-1700。</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">UV-1800外观小巧新颖，流线外形设计。应用Czerny-Turner分光镜，并取得同级别产品中最高级别的分辨率（1nm），实现明亮的光学系统，紧凑的尺寸（安装面积：W450&times;D490mm）。可用作单机仪器，也可计算机控制，内置USB I/F支持USB闪存驱动，加强了与计算机的配合。</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体; font-size: 14px; font-weight: bold;\">■ 主机特点</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;同级别UV中最高分辨率：1nm</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">UV-1800轻松满足欧洲药典要求的波长分辨率。使用带有Czerny-Turner装置的分光镜，得到紧凑、明亮的光学系统。杂散光、波长重复性与基线稳定性也有所提高，满足用户需求。</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;占地空间小：450W&times;490D(mm)<img alt=\"\" align=\"right\" width=\"170\" height=\"200\" src=\"http://www.jskq.com/UserFiles/Image/uv1800_001.jpg\" style=\"margin: 0px; padding: 0px; border: none;\" /></p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">采用Czerny-Turner分光镜，比旧型号UV-1700体积更小，占地空间减少了约15%。</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体;\">&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体;\">&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体;\">&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体;\">&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体;\">&nbsp;&nbsp;</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;数据可优盘保存、计算机处理<img alt=\"\" align=\"right\" width=\"96\" height=\"72\" src=\"http://www.jskq.com/UserFiles/Image/uv1800_002.jpg\" style=\"margin: 0px; padding: 0px; border: none;\" /></p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">单机UV-1800，标配UV Probe计算机处理软件。用UV-1800测定的数据可保存于USB存储器，数据可由计算机与软件UV Probe处理与打印。UV-1800同时具有单机UV如易操作、占地小等优点，与计算机控制UV的性能。</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;可计算机控制</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">可以通过USB接口由计算机控制。UV-1800有已成为计算机标准接口的USB 接口，可由多数商品化计算机控制。</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;USB打印机</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">符合PCL的 USB 接口打印机可用作 UV-1800的打印机，使可用打印机增多。</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体; font-size: 14px; font-weight: bold;\">■ 主要规格</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">测定波长范围:190~1100 nm</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">光谱带宽（分辨率）:1nm</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">波长准确度:&plusmn;0.1 nm（656.1 nm D2）,&plusmn;0.3 nm（全范围）</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">波长重现性:&plusmn;0.1 nm</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">杂散光:0.02%以下（220nm，NaI）、0.02%以下 （340nm，NaNO2）、1%以下（198nm，KCl）</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">测光类型：双光束方式</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">噪声水平：0.00005Abs以内（700nm）</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">检测器：硅晶体光电二极管</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">尺寸：450&times;490&times;270（mm）（W&times;D&times;H）</p>\r\n<p>&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体;\">&nbsp;</p>', 'images/201705/thumb_img/66_thumb_G_1493838888093.jpg', '', 'images/201705/goods_img/66_G_1493838888283.jpg', 'images/201705/source_img/66_G_1493838888904.jpg', '1', '', '1', '1', '0', '0', '1493838888', '100', '0', '1', '1', '1', '0', '0', '1493839464', '2', '', '-1', '-1', '0', '0');
