INSERT INTO `md_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_thumb2`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('69', '2', 'ECS000069', '北分瑞利 SY-8100 高效液相色谱仪', '+', '0', '53', '', '1', '0.000', '0.00', '0.00', '0.00', '0', '0', '1', '', '', '<p>&nbsp;<span style=\"font-family: Arial, Helvetica, 宋体; font-size: 14px; font-weight: bold;\">仪器简介</span></p>\r\n<div style=\"margin: 0px; padding: 0px; font-family: Arial, Helvetica, 宋体; line-height: 22px; text-indent: 2em; letter-spacing: 1px; font-size: 14px;\">\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px;\">科学合理的功能及结构设计，最大程度满足精密分析仪器实验所要求的准确度及重现性。</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px;\">积木式的模块化设计，每一个模块均能单独使用，维护方便。</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px;\">中文界面、功能强大的仪器反控及色谱数据处理工作站，使得仪器控制和数据处理浑然一体。</p>\r\n</div>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体;\">&nbsp;</p>\r\n<p class=\"b\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-weight: bold; font-family: Arial, Helvetica, 宋体;\">创新</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;高压输液泵采用电子脉动抑制技术，取代了传统的机械缓冲器，彻底解决了由于机械缓冲器频繁泄露所导致的重大故障。另外这一新技术的采用也使得输液系统的死体积降到了最低。</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;多波长紫外分光检测器波长定位方式直接由单片机通过控制光栅的转动来实现(即波长与光栅转角的正弦关系通过软件实现)，取代了传统的正弦杆机构(单色仪)，克服了丝杆传动的机械磨损及回差所带来的波长定位误差，也克服了误操作所导致的单色仪撞机事故。</p>\r\n<div style=\"margin: 0px; padding: 0px; font-family: Arial, Helvetica, 宋体;\">&nbsp;</div>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体;\">&nbsp;</p>\r\n<p class=\"b\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-weight: bold; font-family: Arial, Helvetica, 宋体;\">反控及色谱数据处理工作站</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;柱温箱 半导体模块控温方式。</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;完善的反控功能，可对包括柱温箱在内的所有部件参数控制和实时检测。</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;中英文操作界面切换，操作简便。</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;一次进样分析完成，可对色谱数据自动处理，得到完美谱图。</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;后台操作功能，进样分析的同时可对工作站所保存的谱图进行再处理。</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;对输液泵梯度程序和检测器波长时间程序进行设置。</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;可自由设计报告界面。</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;定时关机功能。</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体;\">&nbsp;</p>\r\n<p class=\"b\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-weight: bold; font-family: Arial, Helvetica, 宋体;\">性能特点</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;先进的双柱塞串联式设计以及电子脉动抑制技术的采用，大大提高了高压输液泵的流量精度和重现性。</p>\r\n<p class=\"t\" style=\"margin: 0px; padding: 0px 0px 10px; font-size: 14px; line-height: 18px; letter-spacing: 1px; font-family: Arial, Helvetica, 宋体; text-indent: 2em;\">&bull;&nbsp;全数字化的信号处理及先进的波长控制技术，使色谱数据的可靠性得到更有力的保障。</p>', 'images/201705/thumb_img/69_thumb_G_1493839350142.jpg', '', 'images/201705/goods_img/69_G_1493839350135.jpg', 'images/201705/source_img/69_G_1493839350274.jpg', '1', '', '1', '1', '0', '0', '1493839350', '100', '0', '1', '1', '1', '0', '0', '1493839475', '2', '', '-1', '-1', '0', '0');
