INSERT INTO `md_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_thumb2`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('71', '113', 'ECS000071', '岛津(Shimadzu) InertCap 1 毛细管柱', '+', '0', '12', '', '50', '0.000', '0.00', '0.00', '0.00', '0', '0', '1', '', '', '<p>&nbsp;<span style=\"font-family: arial, helvetica, sans-serif;\">InertCap 1是键合了100%二甲基聚硅氧烷的非极性毛细管柱，样品组分按照沸点高低出峰。具有广泛的适用性，可以作为常规分析柱，适用于NY/T 761中有机氯，拟除虫菊酯的分析。</span></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体;\"><span style=\"margin: 0px; padding: 0px; font-family: arial, helvetica, sans-serif;\"><font size=\"2\" style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 12px;\"><span style=\"margin: 0px; padding: 0px;\">※高惰性</span><br style=\"margin: 0px; padding: 0px;\" />\r\n独特的内壁表面去活技术彻底清除了残余在管内表面的卤代化合物，硅烷醇基团和金属粒子，因此，即使具有强吸附性的极性化合物、酸性、碱性以及金属配位性化合物也能得到优异的对称峰型。<br style=\"margin: 0px; padding: 0px;\" />\r\n<span style=\"margin: 0px; padding: 0px;\">※低流失</span><br style=\"margin: 0px; padding: 0px;\" />\r\n在进行GC/MS分析时，为提高信噪比和检出限，同时为了防止质谱部分的污染，选择具有低流失的毛细管柱是十分有必要的，毛细管柱的流失率将直接影响到MS的灵敏度。随着毛细管柱流失率增加，检测响应信号也随之降低。柱流失是由柱内固定相的溶出造成的，本公司凭借优良的聚合技术，实现了InertCap系列GC/MS用毛细管柱的超低流失的特性。</span><br style=\"margin: 0px; padding: 0px;\" />\r\n</font><font size=\"2\" style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 12px;\"><span style=\"margin: 0px; padding: 0px;\">※品质保证</span><br style=\"margin: 0px; padding: 0px;\" />\r\n在获得ISO9001质量管理<span style=\"margin: 0px; padding: 0px;\">体系认证的日本工厂，</span></span><span style=\"margin: 0px; padding: 0px; font-size: 12px;\">全部采用易产生吸附作用的样品，例如醇类、二醇类、苯酚类以及伯胺类化合物对每一根InertCap系列毛细管柱进行严格的检验测试，每根柱包装内都附带有单个的检验报告。</span></font></span></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体;\"><font size=\"2\" style=\"margin: 0px; padding: 0px;\">&nbsp;</font></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体;\">&nbsp;</p>\r\n<hr style=\"margin: 0px; padding: 0px; font-family: Arial, Helvetica, 宋体;\" />\r\n<p>&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体;\">&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; line-height: 18px; font-family: Arial, Helvetica, 宋体;\"><span style=\"margin: 0px; padding: 0px;\"><font size=\"2\" style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px;\"><span style=\"margin: 0px; padding: 0px; color: rgb(255, 0, 0);\">色谱柱参数：&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><br style=\"margin: 0px; padding: 0px;\" />\r\n<span style=\"margin: 0px; padding: 0px; font-size: 12px;\"><span style=\"margin: 0px; padding: 0px; font-family: arial, helvetica, sans-serif;\"><span style=\"margin: 0px; padding: 0px;\">※</span></span></span></font></span><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: arial, helvetica, sans-serif;\"><font size=\"2\" style=\"margin: 0px; padding: 0px;\">100%二甲基聚硅氧烷<br style=\"margin: 0px; padding: 0px;\" />\r\n<span style=\"margin: 0px; padding: 0px;\">※</span>与USP G2固定相相当&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbs